#include <vector>
#include <limits>
#include <functional>

#include "utils/utils.h"

class Solution {
public:
    int minimumEffortPath(std::vector<std::vector<int>>& heights) {
        const auto rows = heights.size();
        const auto columns = heights[0].size();

        std::vector<std::vector<int>> dp(rows, std::vector<int>(columns, std::numeric_limits<int>::max()));
        std::vector<std::vector<bool>> visited(rows, std::vector<bool>(columns));

        dp[0][0] = 0;

        std::function<void(std::pair<int, int>, std::pair<int, int>)> bfs;
        bfs = [&visited, &dp, &heights, &bfs, columns, rows] (std::pair<int, int> cur_pos, std::pair<int, int> prev_pos) {
            auto [row, column] = cur_pos;
            auto [row_prev, column_prev] = prev_pos;

            if (visited[row][column]) return;

            auto val = std::max(dp[row_prev][column_prev], std::abs(heights[row][column] - heights[row_prev][column_prev]));
            if (val >= dp[row][column]) return;

            dp[row][column] = val;
            visited[row][column] = true;

            // left
            if (column > 0) {
                bfs({row, column - 1}, cur_pos);
            }
            // right
            if (column < columns - 1) {
                bfs({row, column + 1}, cur_pos);
            }
            // top
            if (row > 0) {
                bfs({row - 1, column}, cur_pos);
            }
            // bottom
            if (row < rows - 1) {
                bfs({row + 1, column}, cur_pos);
            }

            visited[row][column] = false;
        };

        if (columns > 1) bfs({0, 1}, {0, 0});
        if (rows > 1) bfs({1, 0}, {0, 0});

        return dp[rows - 1][columns - 1];
    }
};

